css:
table {
    width: 100%;
}
table, th, td {
    border: 0;
}
h2 {
    font-size: 13pt;
}
.header {
    font-size: 10pt;
    padding: 0;
    border-bottom: 1px solid;
    margin-bottom: -20px;
}
.header-small {
    font-size: 7pt;
    padding: 0;
    margin: 0;
    margin-top: -10px;
    vertical-align: top;
}
.right {
    font-size: 12pt;
    text-align: right;
    vertical-align: bottom;
}
.left {
    font-size: 12pt;
    text-align: left;
    vertical-align: bottom;
}
.center {
    font-size: 12pt;
    text-align: center;
}
.common {
    font-size: 12pt;
    text-align: justify;
    width: 100%;
    line-height: 1.6;
    text-indent: 3mm;
    text-align-last: left;
}
.signature {
    width: 42.04mm;
    height: 42.04mm;
    padding: 0;
}
