css:
.print-format {
    font-size: 2.9mm;
    background: white;
}
.print-format td, .print-format th {
    padding: 0 !important;
}

table {
    width: 100%;
}
.header-small {
    font-size: 1rem;
    vertical-align: bottom;
}
.right {
    width: 50%;
    text-align: right;
    vertical-align: top;
    font-size: 1em;
}
.left {
    width: 50%;
    text-align: left;
    vertical-align: top;
    font-size: 1em;
}
.center {
    text-align: center;
    font-size: 1em;
}
.common {
    text-align: justify;
    width: 100%;
    line-height: 1em;
    text-indent: 1em;
    text-align-last: left;
    font-size: 1em;
}
.normal-common {
    text-align: justify;
    width: 100%;
    line-height: 1.2em;
    text-indent: 1em;
    text-align-last: left;
    font-size: 1em;
}
.signature {
    width: 42.04mm;
    height: 42.04mm;
    vertical-align: bottom;
    text-align: center;
}
